import { Component, OnInit } from '@angular/core';
import { Employee } from './../../models/employee';
import { EmployeeService } from './../../services/employee.service';
import { Router } from '@angular/router';

//generated from the command ng g c create-employee in the app folder

@Component({
	selector: 'app-create-employee',
	templateUrl: './create-employee.component.html',
	styleUrls: ['./create-employee.component.scss']
})

export class CreateEmployeeComponent implements OnInit {

	employee: Employee = new Employee();
	constructor(private employeeService: EmployeeService,
		private router: Router) { }

	ngOnInit(): void {
	}

	saveEmployee() {
		this.employeeService.createEmployee(this.employee).subscribe(data => {
			console.log(data);
			this.goToEmployeeList();
		},
			error => console.log(error));
	}

	goToEmployeeList() {
		this.router.navigate(['/employees']);
	}

	onSubmit() {
		console.log(this.employee);
		this.saveEmployee();
	}
}
<div class="row">
	<div class="card col-md-6 offset-md-3 offset-md-3">
		<div class="row">
			<h3 class="text-center">Create Employee</h3>
			<hr />
			<div class="card-body">
				<form (ngSubmit)="onSubmit()">

					<div class="form-group">
						<label> First Name</label> <input type="text" class="form-control"
							id="firstName" [(ngModel)]="employee.firstName" name="firstName">
					</div>

					<div class="form-group">
						<label> Last Name</label> <input type="text" class="form-control"
							id="lastName" [(ngModel)]="employee.lastName" name="lastName">
					</div>

					<div class="form-group">
						<label> Email Id</label> <input type="text" class="form-control"
							id="emailId" [(ngModel)]="employee.emailId" name="emailId">
					</div>

					<br />
					<button class="btn btn-success" type="submit">Submit</button>

				</form>
			</div>
		</div>
	</div>
</div>
